    "position": "absolute",
    "top": "50%",
    "bottom": "50%",
    "zIndex": "1",
  }
  const nav = useNavigate()
  const creatUser = async () => {

    const saveData = async (data) => {
      try {
        await addDoc(collection(db, 'users'), {
          name: data.name,
          email: data.email,
          contact: data.contact,
          address: data.address,
          userType: "Vendor",
          userid: data.userid,
          image: imageUrl,
          fileName: fileName,
          image1: imageUrl1,
          fileName1: fileName1,
          created: Timestamp.now(),
        }).then(
          () => {
            toast.success('Registered successfully!!')
            nav("/")
          }
        ).catch((err) => {
          console.log(err)
        })
      } catch (err) {
        toast.error("Something went wrong!!")
      }
    }
    await createUserWithEmailAndPassword(auth, email, password)
      .then((userCredentail) => {
        const user = userCredentail.user;
        sessionStorage.setItem("userId", user.uid)
        sessionStorage.setItem("userEmail", user.email)
        sessionStorage.setItem("cart", JSON.stringify([]))
        console.log(user);
        let userdata = {
          userid: user.uid,
          name: name,
          email: email,
          contact: contact,
          address: address,

        }

        saveData(userdata)
      })
      .catch((error) => {
        const errorCode = error.code;
        const errorMessage = error.message;
        console.log(errorCode, errorMessage);
        alert("Error" + errorMessage)
      });
  }
  const handleform = async (e) => {
    e.preventDefault()
    setLoad(true)
    uploadFile()
  }
  const handlePasswordChange = (data) => {
    setPassword(data.target.value)
  };
  const handleAddressChange = (e) => {
    setAddress(e.target.value)
  }
  const handleContactChange = (e) => {
    setContact(e.target.value)
  }
  const uploadFile = () => {
    if (!file || !file1) {
      alert("Please upload an image first!");
    }

    console.log("File", file)
    console.log("File Name", file.name)

    const fileName = `${Date.now()}-${file.name}`
    const storageRef = ref(storage, `/files/${fileName}`);
    const fileName1 = `${Date.now()}-${file1.name}`
    const storageRef1 = ref(storage, `/files/${fileName1}`);

    // progress can be paused and resumed. It also exposes progress updates.
    // Receives the storage reference and the file to upload.
    const uploadTask = uploadBytesResumable(storageRef, file);
    const uploadTask1 = uploadBytesResumable(storageRef1, file1);

    uploadTask.on(
      "state_changed",
      (snapshot) => {
        const percent = Math.round(
          (snapshot.bytesTransferred / snapshot.totalBytes) * 100
        );
        // update progress
      },
      (err) => console.log(err),
      () => {
        // download url
        getDownloadURL(uploadTask.snapshot.ref).then((url) => {
          console.log("URL", url);
          setFileName(fileName)
          setImageUrl(url)
        });
      }
    );
    uploadTask1.on(
      "state_changed",
      (snapshot) => {
        const percent = Math.round(
          (snapshot.bytesTransferred / snapshot.totalBytes) * 100
        );
        // update progress
      },
      (err) => console.log(err),
      () => {
        // download url
        getDownloadURL(uploadTask1.snapshot.ref).then((url) => {
          console.log("URL", url);
          setFileName1(fileName1)
          setImageUrl1(url)
        });
      }
    );
  };
  useEffect(() => {
    if (!!imageUrl && !!imageUrl1)
      creatUser()
  }, [imageUrl, imageUrl1])
  return (
